import { Injectable, Inject, PLATFORM_ID } from '@angular/core';
import { BehaviorSubject, Observable, of, throwError } from 'rxjs';
import { delay } from 'rxjs/operators';
import { isPlatformBrowser } from '@angular/common';
import { HttpClient } from "@angular/common/http";



export interface MockUser { // for mock user
  id: string;
  email: string;
  firstName?: string;
  lastName?: string;
  token: string;
}


@Injectable({
  providedIn: 'root'
})
export class Auth {
  // Hold current user state
  // when http require here we can add http
  private userSubject = new BehaviorSubject<MockUser | null>(this.getUserFromStorage());
  user$ = this.userSubject.asObservable();

  constructor(@Inject(PLATFORM_ID) private platformId: Object, private http: HttpClient) {}



  /** Simulate backend login */
  login(email: string, password: string): Observable<MockUser> {
    // mork data : replace this with your actual backend API call
    if (email === 'user@example.com' && password === 'password123') {
      const mockUser: MockUser = {
        id: '1',
        email,
        firstName: 'Demo',
        lastName: 'User',
        token: 'mock-jwt-token'
      };
      this.setSession(mockUser);
      return of(mockUser).pipe(delay(500)); // simulate network delay
    }
    // Invalid login
    return throwError(() => new Error('Invalid credentials')).pipe(delay(500));
  }



  /** Simulate backend registration */
  register(formData: any): Observable<MockUser> {
    // Demo: accept any registration and "log the user in"
    const newUser: MockUser = {
      id: String(Math.random()),
      email: formData.email,
      firstName: formData.firstName,
      lastName: formData.lastName,
      token: 'mock-jwt-token'
    };
    this.setSession(newUser);
    return of(newUser).pipe(delay(700));
  }



  /** Log out the user */
  logout() {
    this.clearSession();
    this.userSubject.next(null);
  }


  /** Get currently logged in user */
  getCurrentUser(): MockUser | null {
    return this.userSubject.value;
  }



  /** Whether user is logged in */
  isLoggedIn(): boolean {
    return !!this.getCurrentUser();
  }



  // Session helpers (localStorage + cookie)
  private setSession(user: MockUser) {
    if (isPlatformBrowser(this.platformId)) {
      localStorage.setItem('currentUser', JSON.stringify(user));

      // Set cookie (expires in 7 days)
      const d = new Date();
      d.setTime(d.getTime() + 7 * 24 * 60 * 60 * 1000);
      document.cookie = `token=${user.token};expires=${d.toUTCString()};path=/`;
    }
    this.userSubject.next(user);
  }


  /** Get user from localStorage */
  private getUserFromStorage(): MockUser | null {
    if (isPlatformBrowser(this.platformId)) {
      const json = localStorage.getItem('currentUser');
      if (json) {
        try {
          return JSON.parse(json) as MockUser;
        } catch {
          return null;
        }
      }
    }
    return null;
  }

  private clearSession() {
    if (isPlatformBrowser(this.platformId)) {
      localStorage.removeItem('currentUser');
      this.clearCookie('token');
    }
  }

  private clearCookie(name: string) {
    if (isPlatformBrowser(this.platformId)) { // Only available in the browser
      document.cookie = name + '=; Max-Age=0; path=/;';
    }
  }
}

